mapper.insert:
INSERT
  INTO tb_trx_info
(
       trxcode
	 , if_type
	 , svc_code
	 , mode
	 , trx_sts_code
	 , reg_dt
)
VALUES
(
       #{trxcode}
	 , #{ifType}
	 , #{svcCode}
	 , #{mode}
	 , NVL(#{trxStsCode}, '0001')
	 , current_timestamp
)

mapper.update:
UPDATE tb_trx_info
   SET trx_sts_code = #{trxStsCode}

     , profile_send_dt = current_timestamp


     , vp_recept_dt = current_timestamp


	 , img_send_dt = current_timestamp


     , nonce = #{nonce}


     , vp = #{vp}


     , error_cn = #{errorCn}

     , vp_verify_result = NVL(#{vpVerifyResult}, 'N')
     , udt_dt = current_timestamp
 WHERE trxcode = #{trxcode}
